import React from 'react';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import Header from './components/Header';
import Slideshow from './components/Slideshow';
import FeaturedProducts from './components/FeaturedProducts';
import Newsletter from './components/Newsletter';
import Footer from './components/Footer';
import ProductPage from './pages/ProductPage';


const featuredProducts = [
  { id: 1, name: 'Dell Latitude E3420', price: '$600', image: '../assets/images/Product/dell3240.jpg', description: 'DELL Latitude 3420-14" FHD-11th Gen Intel Core I7-1165G7 Quad-Core-8GB RAM-256GB SSD-Black.' },
  { id: 2, name: 'Hp Elitebook 840 G6', price: '$500', image: '../assets/images/Product/hp.jpg', description: 'A high-quality gadget.' },
  { id: 3, name: 'Lenovo Ideapad', price: '$650', image: '../assets/images/Product/lenovo1.jpg', description: 'Lenovo Ideapad 15 Inch Intel® Celeron 8GB RAM DDR4 - 256 SSD Windows 11' },
  { id: 4, name: 'Hp Elitebook 845 G7', price: '$700', image: '../assets/images/Product/hp845.jpg', description: 'A high-quality gadget.' },
  { id: 5, name: 'Samsung A25', price: '$100', image: '../assets/images/Product/A25.jpg', description: 'A high-quality gadget.' },
  { id: 6, name: 'Samsung A54', price: '$100', image: '../assets/images/Product/A54.jpg', description: 'A high-quality gadget.' },
];

function App() {
  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <Header />
      <Slideshow />
      <FeaturedProducts />
      <Routes>
        <Route path="/product/:productId" element={<ProductDetails />} />
      </Routes>
      <Newsletter />
      <Footer />
    </Router>
  );
}

function ProductDetails() {
  const { productId } = useParams();
  const product = featuredProducts.find((p) => p.id === parseInt(productId));

  return product ? (
    <ProductPage product={product} />
  ) : (
    <div>Product not found</div>
  );
}

export default App;
