import React from 'react';

import './FeaturedProducts.css';

const products = [
    { id: 1, name: 'Dell Latitude E3420', price: '$600', image: '../assets/images/Product/dell3240.jpg', description: 'DELL Latitude 3420-14" FHD-11th Gen Intel Core I7-1165G7 Quad-Core-8GB RAM-256GB SSD-Black.' },
    { id: 1, name: 'Hp Elitebook 840 G6', price: '$500', image: '../assets/images/Product/hp840.jpg', description: 'A high-quality gadget.' },
    { id: 1, name: 'Lenovo Ideapad', price: '$650', image: '../assets/images/Product/lenovo1.jpg', description: 'Lenovo Ideapad 15 Inch Intel® Celeron 8GB RAM DDR4 - 256 SSD Windows 11' },
    { id: 1, name: 'Hp Elitebook 845 G7', price: '$700', image: '../assets/images/Product/hp845.jpg', description: 'A high-quality gadget.' },
    { id: 1, name: 'Hp Elitebook 840 G6', price: '$100', image: '../assets/images/Product/hp840.jpg', description: 'A high-quality gadget.' },
    { id: 1, name: 'Hp Elitebook 840 G6', price: '$100', image: '../assets/images/Product/hp840.jpg', description: 'A high-quality gadget.' },
];

function FeaturedProducts() {
  return (
    <section className="featured-products">
      <h2>Featured Products</h2>
      <div className="products-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image" />
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">{product.price}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default FeaturedProducts;
