import React, { useState, useEffect } from 'react';
import './Slideshow.css'; // Add styles here
import image1 from '../assets/images/image1.jpg';
import image2 from '../assets/images/image2.jpg';
import image3 from '../assets/images/image3.jpg';
import image4 from '../assets/images/image4.jpg';
import image5 from '../assets/images/image5.jpg';
import image6 from '../assets/images/image6.jpg';
import image7 from '../assets/images/image7.jpg';
import image8 from '../assets/images/image8.jpg';
import image9 from '../assets/images/image9.jpg';
import image10 from '../assets/images/image1.jpg';
import image11 from '../assets/images/image1.jpg';
import image12 from '../assets/images/image1.jpg';
import image13 from '../assets/images/image1.jpg';
import image14 from '../assets/images/image1.jpg';
import image15 from '../assets/images/image1.jpg';
import image16 from '../assets/images/image1.jpg';

const images = [
  '../assets/images/image1.jpg',
  '../assets/images/image2.jpg',
  '../assets/images/image3.jpg',
  '../assets/images/image4.jpg',
  '../assets/images/image5.jpg',
  '../assets/images/image6.jpg',
  '../assets/images/image7.jpg',
  '../assets/images/image8.jpg',
  '../assets/images/image9.jpg',
  '../assets/images/image10.jpg',
  '../assets/images/image11.jpg',
  '../assets/images/image12.jpg',
  '../assets/images/image13.jpg',
  '../assets/images/image14.jpg',
  '../assets/images/image15.jpg',
  '../assets/images/image16.jpg',
];

function Slideshow() {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-play functionality to move to the next image every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const goToPrevious = () => {
    setCurrentIndex((currentIndex === 0 ? images.length : currentIndex) - 1);
  };

  const goToNext = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
  };

  return (
    <div className="slideshow-container">
      <img
        src={images[currentIndex]}
        alt={`Slide ${currentIndex + 1}`}
        className="slide-image"
      />
      <button className="nav-button prev" onClick={goToPrevious}>
        &#10094;
      </button>
      <button className="nav-button next" onClick={goToNext}>
        &#10095;
      </button>
      <div className="dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
}

export default Slideshow;
